import React, { Component } from "react";
import PropTypes from "prop-types";

class LoginSection extends Component {
  render() {
    return (
      <div className="LoginSection">
        <h1 className="welcomeTitle">Welcome</h1>
        <UserInputField />
      </div>
    );
  }
}

class CustomTextInput extends React.Component {
  constructor(props) {
    super(props);
    // create a ref to store the textInput DOM element
    this.textInput = React.createRef();

    this.state = { username: "", password: "" };
  }

  handleUsernameChange = e => {
    this.setState({ username: e.target.username });
  };

  handlePasswordChange = e => {
    this.setState({ password: e.target.password });
  };

  focusTextInput = () => {
    // Explicitly focus the text input using the raw DOM API
    // Note: we're accessing "current" to get the DOM node
    this.textInput.current.focus();
  };

  render() {
    // tell React that we want to associate the <input> ref
    // with the `textInput` that we created in the constructor
    return (
      <div>
        <input
          type="text"
          value={this.state.username}
          placeholder="Username"
          onChange={this.handleUsernameChange}
          onKeyUp={this.handleKey}
        />

        <input
          type="text"
          placeholder="Password"
          ref={this.textInput}
          value={this.state.password}
          type="Password"
          onChange={this.handlePasswordChange}
        />
      </div>
    );
  }
}

class UserInputField extends Component {
  constructor(props) {
    super(props);
    this.state = { isLoggedIn: true, username: "", password: "" };
    this.textInput = React.createRef();
  }

  authenticateUser() {
    if (this.state.username === "Admin") {
      if (this.state.password === "Admin") {
        return alert("Hi Admin!");
        // 1 Should we return enum?
      } else {
        return alert("Invalid Password");
      }
    } else {
      return alert("No Such User");
    }
  }

  handleSubmit = event => {
    this.authenticateUser();
    event.preventDefault();
  };

  handlePasswordChange = userInput => {
    this.setState({ password: userInput });
  };

  handleUsernameChange = userInput => {
    this.setState({ username: userInput });
  };

  // HANDLE TEXT ENTER

  handleKey = e => {
    const keyCode = e.keyCode || e.which;
    if (keyCode === 32) {
      // this.focusTextInput();
      alert("HI");
    }
  };

  focusTextInput = () => {
    this.textInput.current.focus();
  };

  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <InputRow
          onInputChange={this.handleUsernameChange}
          rowFunction="UserName"
          value={this.state.username}
          onKeyUp={this.handleKey}
        />
        <InputRow
          ref={this.textInput}
          value={this.state.password}
          onInputChange={this.handlePasswordChange}
          rowFunction="Password"
          type="password"
        />

        <input
          className="btn btn-success col-sm-1"
          type="submit"
          value="submit"
        />

        <div
          className="alert alert-warning alert-dismissible fade hide"
          role="alert"
        >
          <button
            type="button"
            className="close"
            data-dismiss="alert"
            aria-label="Close"
          >
            <span aria-hidden="true">&times;</span>
          </button>
          <strong>Holy guacamole!</strong> You should check in on some of those
          fields below.
        </div>
      </form>
    );
  }
}

class InputRow extends Component {
  static propTypes = {
    value: PropTypes.string.isRequired,
    rowFunction: PropTypes.string.isRequired,
    onInputChange: PropTypes.func.isRequired,
    type: PropTypes.string,
    ref: PropTypes.ref,
    onKeyUp: PropTypes.func
  };

  static defaultProps = {
    type: "text"
  };

  handleOnChange = event => {
    // this.setState({ userInput: event.target.value });
    this.props.onInputChange(event.target.value);
  };

  render() {
    return (
      <div className="form-group row">
        <label
          htmlFor={this.props.rowFunction}
          className="col-sm-1 col-form-label"
        >
          {this.props.rowFunction}
        </label>
        <div className="col-sm-3">
          <input
          onKeyUp={this.props.handleKey}
            ref={this.props.handleKey}
            value={this.props.value}
            onChange={this.handleOnChange}
            type={this.props.type}
            className="form-control"
            id={this.props.rowFunction}
            placeholder={this.props.rowFunction}
          />
        </div>
      </div>
    );
  }
}

export default LoginSection;
