import React, { Component } from "react";

class LoginSection extends Component {
  constructor(props) {
    super(props);
    this.state = { username: "", password: "", isAutoLoggedIn: "" };
  }

  render() {
    return (
      <div className="LoginSection">
        <h1 className="welcomeTitle">Welcome</h1>
        <UserInputField />
      </div>
    );
  }
}

function authenticateUser() {
  if (doesUserExist) {
    if (this.state.password === "Justin") {
      return alert("Justin!");
    } else {
      return alert("Invalid Password");
    }
  } else {
    return alert("No Such User");
  }
}

function doesUserExist(event) {
  if (event.target.username === "Justin") {
    return true;
  }
  return false;
}

class UserInputField extends Component {
  constructor(props) {
    super(props);
    this.state = { isLoggedIn: true, username: "", password: "" };
    this.handlePasswordChange = this.handlePasswordChange.bind(this);
    this.handleUsernameChange = this.handleUsernameChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleSubmit(event) {
    if (this.state.username == "Justin") {
      alert("Hi Justin")
    }
    event.preventDefault();
  }

  handlePasswordChange(userInput) {
    this.setState({ password: userInput });
  }

  handleUsernameChange(userInput) {
    this.setState({ username: userInput });
  }

  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <InputRow
          onInputChange={this.handleUsernameChange}
          rowFunction="UserName"
        />
        <InputRow
          onInputChange={this.handlePasswordChange}
          rowFunction="Password"
        />
        <input
          className="btn btn-success col-sm-1"
          type="submit"
          value="submit"
        />
      </form>
    );
  }
}

class InputRow extends Component {
  constructor(props) {
    super(props);
    this.handleOnChange = this.handleOnChange.bind(this);
  }

  handleOnChange(event) {
    // this.setState({ userInput: event.target.value });
    this.props.onInputChange(event.target.value);
  }

  render() {
    return (
      <div className="form-group row">
        <label for={this.props.rowFunction} className="col-sm-1 col-form-label">
          {this.props.rowFunction}
        </label>
        <div className="col-sm-3">
          <input
            value={this.props.userInput}
            onChange={this.handleOnChange}
            type="text"
            className="form-control"
            id={this.props.rowFunction}
            placeholder={this.props.rowFunction}
          />
        </div>
      </div>
    );
  }
}

export default LoginSection;
