import React, { Component } from "react";

class ChatThread extends Component {
  state = {};
  render() {
    return (
      <React.Fragment>
        <Comment comment={comment} user={user} />
        <Comment comment={secondComment} user={user} />
      </React.Fragment>
    );
  }
}

function UserInfo(props) {
  return (
    <React.Fragment>
      <div className="userInfo">
        <Avatar value={props.value} />
        <div className="userName">{props.value.name}</div>
      </div>
    </React.Fragment>
  );
}

function Avatar(props) {
  return (
    <React.Fragment>
      <img
        className="Avartar"
        src={props.value.avatarUrl}
        alt={props.value.name}
      />
    </React.Fragment>
  );
}

function UserPost(props) {
  return (
    <React.Fragment>
      <div className="comment">{props.value.data}</div>
      <div className="date-time">{formatDate(props.value.date)}</div>
    </React.Fragment>
  );
}

function Comment(props) {
  return (
    <React.Fragment>
      <UserInfo value={props.user} />
      <UserPost value={props.comment} />
    </React.Fragment>
  );
}

const user = {
  name: "Justin",
  avatarUrl: "https://placekitten.com/g/64/64"
};

const comment = {
  data: "I hope you enjoy learning React!",
  date: new Date()
};

const secondComment = {
  data: "Hey I am a Second Comment",
  date: new Date()
};

function formatDate(date) {
  return date.toLocaleTimeString();
}

export default ChatThread;
